import { Link } from 'react-router-dom';
import olive from '../assets/olive.svg';
import lavender from '../assets/lavender.svg';

export default function Header() {
  return (
    <header className="bg-white shadow-md p-4 flex flex-col items-center">
      {/* Logo + Titre */}
      <div className="flex items-center gap-4 mb-2">
        <img src={olive} alt="Olive" className="w-10 h-10" />
        <h1 className="text-4xl font-bold text-[var(--color-lavender)]">Le Provençal</h1>
        <img src={lavender} alt="Lavande" className="w-10 h-10" />
      </div>

      {/* Navigation */}
      <nav className="flex gap-6 text-lg font-semibold">
        <Link to="/" className="hover:text-[var(--color-terra)]">Accueil</Link>
        <Link to="/langue-dictionnaire" className="hover:text-[var(--color-terra)]">Langue & Dictionnaire</Link>
        <Link to="/geographie" className="hover:text-[var(--color-terra)]">Géographie</Link>
        <Link to="/histoire-legendes" className="hover:text-[var(--color-terra)]">Histoire & Légendes</Link>
      </nav>
    </header>
  );
}
