export default function HistoireLegendes() {
  const menu = {
    Histoire: [
      "Avant JC",
      "De JC à 1000, l'ère 1",
      "De 1000 à 1500, l'ère italienne",
      "De 1500 à nos jours, l'ère moderne"
    ],
    Légendes: ["Légende 1", "Légende 2"]
  };

  return (
    <div className="flex">
      <aside className="w-1/4 p-4 border-r">
        {Object.entries(menu).map(([cat, items]) => (
          <div key={cat}>
            <h3 className="font-bold mb-2">{cat}</h3>
            <ul className="mb-4">
              {items.map((item, i) => (
                <li key={i}>
                  <a href={`#${item.replace(/\\s+/g, '-')}`} className="text-blue-500">{item}</a>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </aside>
      <main className="w-3/4 p-4">
        {Object.entries(menu).map(([cat, items]) => (
          <section key={cat}>
            <h2 className="text-xl font-bold mb-4">{cat}</h2>
            {items.map((item, i) => (
              <div key={i} id={item.replace(/\\s+/g, '-')} className="mb-6">
                <h3 className="text-lg font-semibold">{item}</h3>
                <p>Contenu pour {item}...</p>
              </div>
            ))}
          </section>
        ))}
      </main>
    </div>
  );
}
