import { useEffect, useState } from 'react';
import axios from 'axios';

export default function HistoireLegendes() {
  const [menu, setMenu] = useState({});
  const [details, setDetails] = useState({});
  const [histoires, setHistoires] = useState({});

  useEffect(() => {
    axios.get('http://localhost:5000/api/menu-histoires')
      .then(res => setMenu(res.data))
      .catch(err => console.error(err));

    // Charger les détails (titre -> description_courte)
    axios.get('http://localhost:5000/api/histoires-details')
      .then(res => setDetails(res.data))
      .catch(err => console.error(err));
    
    // Charger les histoires complètes
    axios.get('http://localhost:5000/api/histoires')
      .then(res => setHistoires(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="flex">
      {/* Menu latéral */}
      <aside className="w-1/4 p-4 border-r bg-gray-50">
        {Object.entries(menu).map(([typologie, periodes]) => (
          <div key={typologie} className="mb-6">
            <h3 className="font-bold text-lg mb-2">{typologie}</h3>
            <ul>
              {Object.entries(periodes).map(([periode, titres]) => (
                <li key={periode} className="ml-4 mb-2">
                  <strong>{periode}</strong>
                  <ul className="ml-4 list-disc">
                    {titres.map((titre, i) => (
                      <li key={i} className="relative group">
                        <a
                          href={`#${titre.replace(/\s+/g, '-')}`}
                          className="text-blue-600 hover:underline"
                        >
                          {titre}
                        </a>
                        {/* Tooltip au survol */}
                        {details[titre] && (
                          <div className="absolute left-full top-0 ml-2 w-64 p-2 bg-white border rounded shadow-lg opacity-0 group-hover:opacity-100 transition">
                            <p className="text-sm text-gray-700">{details[titre]}</p>
                          </div>
                        )}
                      </li>
                    ))}
                  </ul>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </aside>

      {/* Contenu principal */}
      <main className="w-3/4 p-6">
        {Array.isArray(histoires) && histoires.length > 0 ? (
          // Regrouper par typologie
          Object.entries(
            histoires.reduce((acc, h) => {
              if (!acc[h.typologie]) acc[h.typologie] = {};
              if (!acc[h.typologie][h.periode]) acc[h.typologie][h.periode] = [];
              acc[h.typologie][h.periode].push(h);
              return acc;
            }, {})
          ).map(([typologie, periodes]) => (
            <section key={typologie} className="mb-8">
              <h2 className="text-2xl font-bold mb-4">{typologie}</h2>
              {Object.entries(periodes).map(([periode, histoiresArray]) => (
                <div key={periode} className="mb-6">
                  <h3 className="text-xl font-semibold mb-2">{periode}</h3>
                  {histoiresArray.map((histoire, i) => (
                    <div
                      key={i}
                      id={histoire.titre.replace(/\s+/g, '-')}
                      className="mb-4 p-4 border rounded shadow-sm bg-white"
                    >
                      <h4 className="font-bold text-lg">{histoire.titre}</h4>
                      <p className="mb-2">{histoire.description_longue}</p>
                      {histoire.source_url && (
                        <a
                          href={histoire.source_url}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-blue-600 hover:underline text-sm"
                        >
                          Voir la source
                        </a>
                      )}
                    </div>
                  ))}
                </div>
              ))}
            </section>
          ))
        ) : (
          <p>Chargement des histoires...</p>
        )}
      </main>

    </div>
  );
}
