import { useEffect, useState } from 'react';
import axios from 'axios';

export default function HistoireLegendes() {
  const [menu, setMenu] = useState({});
  const [details, setDetails] = useState({});
  const [histoires, setHistoires] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const limit = 5;

  useEffect(() => {
    axios.get('http://localhost:5000/api/menu-histoires')
      .then(res => setMenu(res.data))
      .catch(err => console.error(err));

    axios.get('http://localhost:5000/api/histoires-details')
      .then(res => setDetails(res.data))
      .catch(err => console.error(err));
  }, []);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/histoires?page=${page}&limit=${limit}`)
      .then(res => {
        setHistoires(res.data.data);
        setTotalPages(res.data.pages);
      })
      .catch(err => console.error(err));
  }, [page]);

  const handleMenuClick = async (titre) => {
    const id = titre.replace(/\s+/g, '-');
    const found = histoires.find(h => h.titre === titre);

    if (found) {
      // Scroll vers l'ancre
      document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' });
    } else {
      // Trouver la page où se trouve ce titre via API
      try {
        const res = await axios.get(`http://localhost:5000/api/find-histoire?titre=${encodeURIComponent(titre)}`);
        const targetPage = res.data.page;
        setPage(targetPage);

        // Attendre le chargement puis scroll
        setTimeout(() => {
          document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' });
        }, 500);
      } catch (err) {
        console.error(err);
      }
    }
  };

  return (
    <div className="flex">
      {/* Menu latéral */}
      <aside className="w-1/4 p-4 border-r bg-gray-50">
        {Object.entries(menu).map(([typologie, periodes]) => (
          <div key={typologie} className="mb-6">
            <h3 className="font-bold text-lg mb-2">{typologie}</h3>
            <ul>
              {Object.entries(periodes).map(([periode, titres]) => (
                <li key={periode} className="ml-4 mb-2">
                  <strong>{periode}</strong>
                  <ul className="ml-4 list-disc">
                    {titres.map((titre, i) => (
                      <li key={i} className="relative group">
                        <button
                          onClick={() => handleMenuClick(titre)}
                          className="text-blue-600 hover:underline"
                        >
                          {titre}
                        </button>
                        {details[titre] && (
                          <div className="absolute left-full top-0 ml-2 w-64 p-2 bg-white border rounded shadow-lg opacity-0 group-hover:opacity-100 transition">
                            <p className="text-sm text-gray-700">{details[titre]}</p>
                          </div>
                        )}
                      </li>
                    ))}
                  </ul>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </aside>

      {/* Contenu principal */}
      <main className="w-3/4 p-6">
        {histoires.length > 0 ? (
          histoires.map((histoire, i) => (
            <div
              key={i}
              id={histoire.titre.replace(/\s+/g, '-')}
              className="mb-4 p-4 border rounded shadow-sm bg-white"
            >
              <h4 className="font-bold text-lg">{histoire.titre}</h4>
              <p className="mb-2">{histoire.description_longue}</p>
              {histoire.source_url && (
                <a
                  href={histoire.source_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 hover:underline text-sm"
                >
                  Voir la source
                </a>
              )}
            </div>
          ))
        ) : (
          <p>Chargement des histoires...</p>
        )}

        {/* Pagination */}
        <div className="flex justify-center mt-6 space-x-2">
          <button
            disabled={page === 1}
            onClick={() => setPage(page - 1)}
            className="px-3 py-1 border rounded bg-gray-200 disabled:opacity-50"
          >
            Précédent
          </button>
          {Array.from({ length: totalPages }, (_, index) => (
            <button
              key={index}
              onClick={() => setPage(index + 1)}
              className={`px-3 py-1 border rounded ${page === index + 1 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
            >
              {index + 1}
            </button>
          ))}
          <button
            disabled={page === totalPages}
            onClick={() => setPage(page + 1)}
            className="px-3 py-1 border rounded bg-gray-200 disabled:opacity-50"
          >
            Suivant
          </button>
        </div>
      </main>
    </div>
  );
}
