import { useEffect, useState } from 'react';
import { getThemes, getCategories, getDictionary } from '../services/api';
import Loader from '../components/Loader';
import Pagination from '../components/Pagination';

export default function DictionaryPage() {
  const [mots, setMots] = useState([]);
  const [themes, setThemes] = useState(['tous']);
  const [categories, setCategories] = useState(['toutes']);
  const [theme, setTheme] = useState('tous');
  const [categorie, setCategorie] = useState('toutes');
  const [lettre, setLettre] = useState('toutes');
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(1);
  const [loading, setLoading] = useState(false);
  const [sort, setSort] = useState('mots_francais');
  const [order, setOrder] = useState('asc');

  const alphabet = ['toutes', ...'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('')];

  // Charger les thèmes
  useEffect(() => {
    getThemes().then(res => setThemes(['tous', ...res.data]));
  }, []);

  // Charger les catégories selon le thème
  useEffect(() => {
    getCategories(theme).then(res => setCategories(['toutes', ...res.data]));
  }, [theme]);

  // Charger les mots
  useEffect(() => {
    setLoading(true);
    getDictionary({ theme, categorie, lettre, search, page, limit: 20, sort, order })
      .then(res => {
        setMots(res.data.data);
        setPages(res.data.pages);
      })
      .finally(() => setLoading(false));
  }, [theme, categorie, lettre, search, page, sort, order]);

  const handleSort = (column) => {
    if (sort === column) {
      setOrder(order === 'asc' ? 'desc' : 'asc');
    } else {
      setSort(column);
      setOrder('asc');
    }
  };

  const sortIcon = (column) => {
    if (sort === column) return order === 'asc' ? '▲' : '▼';
    return '';
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-[var(--color-lavender)] mb-6">
        Dictionnaire Provençal
      </h1>

      {/* Filtres */}
      <div className="flex flex-wrap gap-4 mb-6">
        <select
          value={theme}
          onChange={(e) => { setTheme(e.target.value); setPage(1); }}
          className="border p-2 rounded bg-white"
        >
          {themes.map((t) => <option key={t} value={t}>{t}</option>)}
        </select>

        <select
          value={categorie}
          onChange={(e) => { setCategorie(e.target.value); setPage(1); }}
          className="border p-2 rounded bg-white"
        >
          {categories.map((c) => <option key={c} value={c}>{c}</option>)}
        </select>

        <input
          type="text"
          placeholder="Recherche..."
          value={search}
          onChange={(e) => { setSearch(e.target.value); setPage(1); }}
          className="border p-2 rounded flex-1"
        />
      </div>

      {/* Alphabet */}
      <div className="flex flex-wrap gap-2 mb-6">
        {alphabet.map((l) => (
          <button
            key={l}
            onClick={() => { setLettre(l); setPage(1); }}
            className={`px-3 py-1 border rounded ${lettre === l ? 'bg-[var(--color-sun)]' : 'bg-white'}`}
          >
            {l}
          </button>
        ))}
      </div>

      {/* Loader */}
      {loading && <Loader message="Chargement des mots..." />}

      {/* Table */}
      {!loading && (
        <div className="overflow-x-auto">
          <table className="w-full border-collapse border">
            <thead>
              <tr className="bg-gray-200">
                {['theme', 'categorie', 'mots_francais', 'mots_provencal'].map(col => (
                  <th
                    key={col}
                    className="border p-2 cursor-pointer"
                    onClick={() => handleSort(col)}
                  >
                    {col} {sortIcon(col)}
                  </th>
                ))}
                <th className="border p-2">Description</th>
                <th className="border p-2">Synonymes</th>
                <th className="border p-2">Sources</th>
              </tr>
            </thead>
            <tbody>
              {mots.map((m) => (
                <tr key={m.id}>
                  <td className="border p-2">{m.theme}</td>
                  <td className="border p-2">{m.categorie}</td>
                  <td className="border p-2">{m.mots_francais}</td>
                  <td className="border p-2">{m.mots_provencal}</td>
                  <td className="border p-2">{m.description}</td>
                  <td className="border p-2">{m.synonymes_francais}</td>
                  <td className="border p-2 text-sm text-gray-600">
                    {m.eg_provencal}, {m.d_provencal}, {m.a_provencal}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Pagination */}
      <Pagination page={page} pages={pages} onPageChange={setPage} />
    </div>
  );
}
