import { useAuth } from '../hooks/useAuth';

export default function Header() {
  const { user } = useAuth();
  // ...
  return (
    // ...
    <nav className="flex items-center justify-center gap-8 text-xl font-semibold">
      {/* ... */}
      <div className="flex-1" />
      {user ? (
        <span className="text-sm text-gray-600">Connect√©: {user.username}</span>
      ) : (
        <Link
          to="/login"
          className="px-4 py-2 rounded bg-[var(--color-lavender)] text-white hover:opacity-90 transition"
        >
          Login
        </Link>
      )}
    </nav>
  );
}
