import { useEffect, useState } from 'react';
import axios from 'axios';

export default function HistoireLegendes() {
  const [menu, setMenu] = useState({});
  const [details, setDetails] = useState({});
  const [histoires, setHistoires] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const limit = 5;

  useEffect(() => {
    axios.get('http://localhost:5000/api/menu-histoires')
      .then(res => setMenu(res.data))
      .catch(err => console.error(err));

    axios.get('http://localhost:5000/api/histoires-details')
      .then(res => setDetails(res.data))
      .catch(err => console.error(err));
  }, []);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/histoires?page=${page}&limit=${limit}`)
      .then(res => {
        setHistoires(res.data.data);
        setTotalPages(res.data.pages);
      })
      .catch(err => console.error(err));
  }, [page]);

  return (
    <div className="flex">
      {/* Menu latéral */}
      <aside className="w-1/4 p-4 border-r bg-gray-50">
        {/* ... même logique pour le menu */}
      </aside>

      {/* Contenu principal */}
      <main className="w-3/4 p-6">
        {histoires.length > 0 ? (
          histoires.map((histoire, i) => (
            <div
              key={i}
              id={histoire.titre.replace(/\s+/g, '-')}
              className="mb-4 p-4 border rounded shadow-sm bg-white"
            >
              <h4 className="font-bold text-lg">{histoire.titre}</h4>
              <p className="mb-2">{histoire.description_longue}</p>
              {histoire.source_url && (
                <a
                  href={histoire.source_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 hover:underline text-sm"
                >
                  Voir la source
                </a>
              )}
            </div>
          ))
        ) : (
          <p>Chargement des histoires...</p>
        )}

        {/* Pagination */}
        <div className="flex justify-center mt-6 space-x-2">
          <button
            disabled={page === 1}
            onClick={() => setPage(page - 1)}
            className="px-3 py-1 border rounded bg-gray-200 disabled:opacity-50"
          >
            Précédent
          </button>
          {Array.from({ length: totalPages }, (_, index) => (
            <button
              key={index}
              onClick={() => setPage(index + 1)}
              className={`px-3 py-1 border rounded ${page === index + 1 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
            >
              {index + 1}
            </button>
          ))}
          <button
            disabled={page === totalPages}
            onClick={() => setPage(page + 1)}
            className="px-3 py-1 border rounded bg-gray-200 disabled:opacity-50"
          >
            Suivant
          </button>
        </div>
      </main>
    </div>
  );
}
