export default function ArticleCard({ titre, description, image_url, source_url }) {
  return (
    <div className="flex gap-4 p-4 border rounded">
      <img src={image_url} alt={titre} className="w-24 h-24 object-cover" />
      <div>
        <h3 className="font-bold">{titre}</h3>
        <p>{description}</p>
        <a href={source_url} className="text-blue-500">Lire la suite</a>
      </div>
    </div>
  );
}
