import { useEffect, useState } from 'react';
import axios from 'axios';

export default function LangueDictionnaire() {
  const [francais, setFrancais] = useState('');
  const [provencal, setProvencal] = useState('');
  const [mots, setMots] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/dictionnaire')
      .then(res => setMots(res.data));
  }, []);

  return (
    <div className="p-6">
      <div className="flex gap-4 mb-6">
        <textarea
          placeholder="Texte en français"
          value={francais}
          onChange={(e) => setFrancais(e.target.value)}
          className="w-1/2 p-2 border rounded"
        />
        <textarea
          placeholder="Texte en provençal"
          value={provencal}
          onChange={(e) => setProvencal(e.target.value)}
          className="w-1/2 p-2 border rounded"
        />
      </div>
      <table className="w-full border-collapse border">
        <thead>
          <tr className="bg-gray-200">
            <th className="border p-2">Français</th>
            <th className="border p-2">Provençal</th>
            <th className="border p-2">Catégorie</th>
            <th className="border p-2">Exemple</th>
          </tr>
        </thead>
        <tbody>
          {mots.map((m, i) => (
            <tr key={i}>
              <td className="border p-2">{m.mot_francais}</td>
              <td className="border p-2">{m.mot_provencal}</td>
              <td className="border p-2">{m.categorie}</td>
              <td className="border p-2">{m.exemple}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
