import { useEffect, useState } from 'react';
import axios from 'axios';

export default function LangueDictionnaire() {
  const [mots, setMots] = useState([]);
  const [themes, setThemes] = useState([]);
  const [categories, setCategories] = useState([]);
  const [theme, setTheme] = useState('tous');
  const [categorie, setCategorie] = useState('toutes');
  const [lettre, setLettre] = useState('toutes');
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(1);
  const [loading, setLoading] = useState(false);
  const [sort, setSort] = useState('mots_francais');
  const [order, setOrder] = useState('asc');

  const alphabet = ['toutes', ...'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('')];

  useEffect(() => {
    axios.get('http://localhost:5000/api/themes').then(res => setThemes(['tous', ...res.data]));
  }, []);
  
  useEffect(() => {
    axios.get('http://localhost:5000/api/categories', {
      params: { theme }
    }).then(res => setCategories(['toutes', ...res.data]));
  }, [theme]);

  useEffect(() => {
    setLoading(true);
    axios.get('http://localhost:5000/api/dictionnaire', {
      params: { theme, categorie, lettre, search, page, limit: 20, sort, order }
    }).then(res => {
      setMots(res.data.data);
      setPages(res.data.pages);
      setLoading(false);
    });
  }, [theme, categorie, lettre, search, page, sort, order]);

  const handleSort = (column) => {
    if (sort === column) {
      setOrder(order === 'asc' ? 'desc' : 'asc');
    } else {
      setSort(column);
      setOrder('asc');
    }
  };

  const sortIcon = (column) => {
    if (sort === column) {
      return order === 'asc' ? '▲' : '▼';
    }
    return '';
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Dictionnaire Provençal</h1>

      {/* Filtres */}
      <div className="flex gap-4 mb-4">
        <select value={theme} onChange={(e) => { setTheme(e.target.value); setPage(1); }} className="border p-2 rounded">
          {themes.map((t, i) => <option key={i} value={t}>{t}</option>)}
        </select>

        <select value={categorie} onChange={(e) => { setCategorie(e.target.value); setPage(1); }} className="border p-2 rounded">
          {categories.map((c, i) => <option key={i} value={c}>{c}</option>)}
        </select>

        <input
          type="text"
          placeholder="Recherche..."
          value={search}
          onChange={(e) => { setSearch(e.target.value); setPage(1); }}
          className="border p-2 rounded flex-1"
        />
      </div>

      {/* Alphabet */}
      <div className="flex flex-wrap gap-2 mb-6">
        {alphabet.map((l) => (
          <button
            key={l}
            onClick={() => { setLettre(l); setPage(1); }}
            className={`px-2 py-1 border rounded ${lettre === l ? 'bg-blue-200' : 'bg-white'}`}
          >
            {l}
          </button>
        ))}
      </div>

      {/* Loader */}
      {loading && <p className="text-gray-500">Chargement...</p>}

      {/* Table */}
      {!loading && (
        <table className="w-full border-collapse border">
          <thead>
            <tr className="bg-gray-200">
              <th className="border p-2 cursor-pointer" onClick={() => handleSort('theme')}>Thème {sortIcon('theme')}</th>
              <th className="border p-2 cursor-pointer" onClick={() => handleSort('categorie')}>Catégorie {sortIcon('categorie')}</th>
              <th className="border p-2 cursor-pointer" onClick={() => handleSort('mots_francais')}>Français {sortIcon('mots_francais')}</th>
              <th className="border p-2 cursor-pointer" onClick={() => handleSort('mots_provencal')}>Provençal {sortIcon('mots_provencal')}</th>
              <th className="border p-2">Description</th>
              <th className="border p-2">Synonymes</th>
              <th className="border p-2">Sources</th>
            </tr>
          </thead>
          <tbody>
            {mots.map((m, i) => (
              <tr key={i}>
                <td className="border p-2">{m.theme}</td>
                <td className="border p-2">{m.categorie}</td>
                <td className="border p-2">{m.mots_francais}</td>
                <td className="border p-2">{m.mots_provencal}</td>
                <td className="border p-2">{m.description}</td>
                <td className="border p-2">{m.synonymes_francais}</td>
                <td className="border p-2">
                  {m.eg_provencal}, {m.d_provencal}, {m.a_provencal}, {m.h_provencal}, {m.av_provencal}, {m.p_provencal}, {m.x_provencal}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Pagination */}
      <div className="flex justify-center gap-2 mt-4">
        <button disabled={page === 1} onClick={() => setPage(page - 1)} className="px-3 py-1 border rounded">Précédent</button>
        <span>Page {page} / {pages}</span>
        <button disabled={page === pages} onClick={() => setPage(page + 1)} className="px-3 py-1 border rounded">Suivant</button>
      </div>
    </div>
  );
}
